import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import Button from './Button';

const HeroSlider = (props) => {
    const data = props.data;

    const timeOut = props.timeOut ? props.timeOut : 5000;

    const [activeSilde, setActiveSilde] = useState(0);

    const nextSlide = () => {
        const index = activeSilde + 1 === data.length ? 0 : activeSilde + 1;
        setActiveSilde(index);
    };
    const prevSlide = () => {
        const index = activeSilde - 1 < 0 ? data.length - 1 : activeSilde - 1;
        setActiveSilde(index);
    };

    useEffect(() => {
        if (props.auto) {
            const slideAuto = setInterval(() => {
                nextSlide();
            }, timeOut);
            return () => {
                clearInterval(slideAuto);
            };
        }
    }, [activeSilde, timeOut]);

    return (
        <div className="hero-slider">
            {data.map((item, index) => {
                const active = index === activeSilde;
                return (
                    <div className={`hero-slider__item ${active ? 'active' : ''}`}>
                        <div className="hero-slider__item__info">
                            <div className={`hero-slider__item__info__title color-${item.color}`}>
                                <span>{item.title}</span>
                            </div>
                            <div className="hero-slider__item__info__description">
                                <span>{item.description}</span>
                            </div>
                            <div className="hero-slider__item__info__btn">
                                <Link to={item.path}>
                                    <Button backGroundColor={item.color} icon="bx bx-cart" animate={true}>
                                        Xem chi tiáº¿t
                                    </Button>
                                </Link>
                            </div>
                        </div>
                        <div className="hero-slider__item__image">
                            <div className={`shape bg-${item.color}`}></div>
                            <img src={item.img} alt=""></img>
                        </div>
                    </div>
                );
            })}
            {
                <div className="hero-slider__control">
                    <div className="hero-slider__control__item" onClick={prevSlide}>
                        <i className="bx bx-chevron-left"></i>
                    </div>
                    <div className="hero-slider__control__item">
                        <div className="index">
                            {activeSilde + 1}/{data.length}
                        </div>
                    </div>
                    <div className="hero-slider__control__item" onClick={nextSlide}>
                        <i className="bx bx-chevron-right"></i>
                    </div>
                </div>
            }
        </div>
    );
};

HeroSlider.propTypes = {
    data: PropTypes.array.isRequired,
    control: PropTypes.bool,
    auto: PropTypes.bool,
    timeOut: PropTypes.number,
};

export default HeroSlider;
